INSERT INTO `questions` VALUES (1,1,'映射的定义是什么？','{\"A\": \"任何两个集合之间的对应关系\", \"B\": \"对X中的每个元素，都有唯一的Y与之对应\", \"C\": \"X和Y之间可以任意匹配\", \"D\": \"X中的一个元素可以对应多个Y\"}','B'),(2,1,'在映射中，X 和 Y 分别指什么？','{\"A\": \"X 是值域，Y 是定义域\", \"B\": \"X 是非空集合，Y 是非空集合\", \"C\": \"X 是函数，Y 是变量\", \"D\": \"X 是范围，Y 是变量\"}','B'),(3,1,'下面哪个符号正确表示映射的表达方式？','{\"A\": \"X → F → Y\", \"B\": \"X = F(Y)\", \"C\": \"F: X → Y\", \"D\": \"Y: X → F\"}','C'),(4,1,'在映射中，\"元相\"指的是哪个集合的元素？','{\"A\": \"X\", \"B\": \"Y\", \"C\": \"F\", \"D\": \"Z\"}','A'),(5,1,'下面哪项不是映射必须满足的条件？','{\"A\": \"每个 X 中的元素必须有一个 Y 与之对应\", \"B\": \"一个 X 不能有多个不同的 Y\", \"C\": \"每个 Y 都必须有一个 X 对应\", \"D\": \"映射需要一个对应规则\"}','C'),(6,1,'满射（Surjective）的定义是？','{\"A\": \"每个 X 都有唯一的 Y\", \"B\": \"X 中的元素可以映射到多个 Y\", \"C\": \"值域等于 Y\", \"D\": \"X 与 Y 之间的映射是单射\"}','C'),(7,1,'单射（Injective）映射的特点是？','{\"A\": \"每个 X 对应唯一的 Y\", \"B\": \"允许不同的 X 对应相同的 Y\", \"C\": \"不允许不同的 X 对应相同的 Y\", \"D\": \"只有一个 X 没有对应的 Y\"}','C'),(8,1,'一一映射（Bijective）是指？','{\"A\": \"既是单射又是满射\", \"B\": \"只有一个 X 没有 Y\", \"C\": \"允许 X 和 Y 之间有多个对应关系\", \"D\": \"只要求 X 有唯一的 Y 对应\"}','A'),(9,1,'在示例中，为什么\"一夫两妻\"不被允许？','{\"A\": \"因为不符合数学规则\", \"B\": \"因为它不是单射\", \"C\": \"因为它不是满射\", \"D\": \"因为它是一个特殊情况\"}','B'),(10,1,'在映射的属性中，\"定义域\"通常指的是？','{\"A\": \"X\", \"B\": \"Y\", \"C\": \"F\", \"D\": \"Z\"}','A'),(11,1,'逆映射存在的必要条件是什么？','{\"A\": \"满射\", \"B\": \"单射\", \"C\": \"任意映射都可以有逆映射\", \"D\": \"以上都不对\"}','B'),(12,1,'下面哪个不是映射的示例？','{\"A\": \"每个女生对应一个男生\", \"B\": \"一个 X 对应两个 Y\", \"C\": \"一个 X 只对应一个 Y\", \"D\": \"一个 Y 只对应一个 X\"}','B'),(13,1,'符合映射的定义是什么？','{\"A\": \"两个映射组合而成\", \"B\": \"只能在单射的情况下使用\", \"C\": \"必须包含满射\", \"D\": \"只适用于一一映射\"}','A'),(14,1,'下面哪个是符合映射的示例？','{\"A\": \"Y = sin(X²)\", \"B\": \"Y = X + 1\", \"C\": \"Y = X * 2\", \"D\": \"Y = log(X)\"}','A'),(15,1,'值域（Codomain）指的是？','{\"A\": \"X 中的所有元素\", \"B\": \"Y 中可能被 X 映射到的所有元素\", \"C\": \"X 和 Y 之间的关系\", \"D\": \"X 和 Y 的所有可能组合\"}','B'),(16,1,'下面哪种情况符合\"满射\"？','{\"A\": \"X 中的所有元素都映射到 Y，且 Y 中所有元素都有 X 对应\", \"B\": \"X 中的元素映射到部分 Y\", \"C\": \"X 中部分元素映射到 Y\", \"D\": \"X 和 Y 之间没有任何关系\"}','A'),(17,1,'在映射关系中，F 指的是？','{\"A\": \"变量\", \"B\": \"映射的对应规则\", \"C\": \"定义域\", \"D\": \"值域\"}','B'),(18,1,'下面哪种说法是正确的？','{\"A\": \"每个满射都是单射\", \"B\": \"每个单射都是满射\", \"C\": \"一一映射必须是单射和满射\", \"D\": \"满射和单射没有关系\"}','C'),(19,1,'\"两夫一妻\"为什么被允许？','{\"A\": \"因为它符合单射的定义\", \"B\": \"因为它符合满射的定义\", \"C\": \"因为不同的 X 可以对应同一个 Y\", \"D\": \"因为它是符合映射\"}','C'),(20,1,'为什么映射是数学分析的重要内容？','{\"A\": \"因为它可以帮助理解函数关系\", \"B\": \"因为它能表示任何数学问题\", \"C\": \"因为所有的数学运算都可以用映射表示\", \"D\": \"因为它只适用于高中数学\"}','A'),(21,12,'量子场论中的场是指什么？','{\"A\": \"空间中的一个点\", \"B\": \"时空中每一点都有定义的物理量\", \"C\": \"粒子的位置\", \"D\": \"波函数\"}','B'),(22,12,'量子场论统一了哪些理论？','{\"A\": \"量子力学和狭义相对论\", \"B\": \"经典力学和量子力学\", \"C\": \"广义相对论和量子力学\", \"D\": \"电磁学和量子力学\"}','A'),(23,12,'在量子场论中，粒子被视为什么？','{\"A\": \"点状物体\", \"B\": \"波包\", \"C\": \"场的激发态\", \"D\": \"经典物体\"}','C'),(24,12,'量子场论中的真空态是指？','{\"A\": \"完全没有粒子的状态\", \"B\": \"最低能量状态\", \"C\": \"基态\", \"D\": \"以上都对\"}','D'),(25,12,'费曼图是用来描述什么的？','{\"A\": \"粒子轨迹\", \"B\": \"场的传播\", \"C\": \"粒子相互作用过程\", \"D\": \"波函数坍缩\"}','C'),(26,12,'量子场论中的规范不变性指的是什么？','{\"A\": \"物理定律在坐标变换下不变\", \"B\": \"物理定律在局部相位变换下不变\", \"C\": \"物理定律在时间反演下不变\", \"D\": \"物理定律在宇称变换下不变\"}','B'),(27,12,'量子电动力学描述的是什么相互作用？','{\"A\": \"强相互作用\", \"B\": \"弱相互作用\", \"C\": \"电磁相互作用\", \"D\": \"引力相互作用\"}','C'),(28,12,'在量子场论中，自旋是如何引入的？','{\"A\": \"作为额外的自由度\", \"B\": \"通过场方程的解\", \"C\": \"通过对称性要求\", \"D\": \"通过实验观测\"}','C'),(29,12,'量子场论中的发散问题如何处理？','{\"A\": \"忽略高能贡献\", \"B\": \"引入截断\", \"C\": \"重整化\", \"D\": \"数值模拟\"}','C'),(30,12,'路径积分方法的物理意义是什么？','{\"A\": \"所有可能路径的叠加\", \"B\": \"最小作用量原理\", \"C\": \"波函数演化\", \"D\": \"粒子运动轨迹\"}','A');
